void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;




    //shape position
    vec2 shapeCenter = vec2( 0.5, 0.5);
    //distance from pixel to shape center
    ////vec2 distanceToShape = p - shapeCenter;
    vec2 distanceToShape = p - shapeCenter;

    //color vector
    vec3 col = vec3( 0.0, 1.0, 0.0 );

    //shape radius
    float shapeRadius = 0.2 + 0.1*cos( atan(distanceToShape.x, distanceToShape.y)*10.00
                           - 20.00*distanceToShape.x*iTime + 10000000.0  );
    //shape sharpness (smaller is sharper)
    float sharpnessMod = 0.008;

    col *=  1.00 - smoothstep( shapeRadius, shapeRadius+sharpnessMod, length(distanceToShape));
    //col = vec3( 1.0, 1.0, 1.0) - col;

    //finally changes the color
	fragColor = vec4(col,1.0);
}
