void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 div = vec2( 6.*iResolution.x/iResolution.y, 6. );
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 xy = div*uv;
    vec2 v = fract( xy );
    float c;
    if ( (v.x + v.y)*0.5 > 0.5 )
        c = 1.0;
    else
        c= 0.0;
	fragColor = vec4(c,c,c,1);
}
