#define rnd(x) ( 2.* fract(456.68*sin(1e4*x+mod(iDate.w,100.))) -1.) // NB: mod(t,1.) for less packed pattern

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    //if(iTime < 0.1){

    float f = 1. + 0.1*rnd(fragCoord.x/300.+fragCoord.y*2.);
    float angle = -iTime/f + 0.1*rnd(fragCoord.x/100.+fragCoord.y);
    vec2 pos = vec2( cos(angle), sin(angle) ) * 0.5;
    vec4 a = texture( iChannel0, uv );
    //vec4 b = texture( iChannel0, vec2(uv.x+1.0/iResolution.x*4., uv.y) );
    //vec4 c = texture( iChannel0, vec2(uv.x, uv.y+1.0/iResolution.y*4.) );
    vec4 b = texture( iChannel0, vec2(
        uv.x + 1. / iResolution.x * pos.x,
        uv.y + 1. / iResolution.y * pos.y
    ) );

    //fragColor = mix( mix(a, b, 0.5), mix(a, c, 0.5), 0.5 );
    fragColor = mix(a, b, 0.75);
    fragColor.r += 0.2 * rnd(fragCoord.x+fragCoord.y);
    fragColor.g += a.r / 7.;
    fragColor.b += a.r / 3.;

    if( fragColor.r > 0.99 )
        fragColor.r -= 0.9;
    if( fragColor.g > 0.99 )
        fragColor.g -= 0.9;
    if( fragColor.b > 0.99 )
        fragColor.b -= 0.9;

    if( fragColor.r+fragColor.g+fragColor.b < 0.001 ){
        fragColor = vec4( rnd(fragCoord.x+fragCoord.y), 0.0, 0.0, 1.0 );
        return;
    }

    //fragColor = vec4( 0.0, 0.0, 1.0, 1.0 );
}
