void mainImage( out vec4 c, vec2 f ){
    vec3 m, p=m=vec3(0.,5,iTime);
    float i = 1., l;
    while(i++ < 1e2)
        c = texture( iChannel0,p.xz/1e3 + iTime/5e2 ),
        l = length(vec3(fract(p.xz)-.5, mod(p.y +c*4.,7.)-3.5))-.7,
        p += (vec3(f/iResolution.y,1)-.5)*l;

    l = -.6 - length( p - m ) / 70.;
    l *= l;
    c *= l * l * l * 2.;
}
