//apparently bitwise operations aren't supported before GL version 3.0...
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float t = iTime/2.;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;

    uv.y /= iResolution.x/iResolution.y;
    uv *= 2.*sin(t/5.)+3.;

    float s = sin(t/3.), c = cos(t/3.);
    uv *= mat2(s,c,c,-s);

    uv = abs(fract(abs(uv))-.5);

    s = sin(t-uv.x*4.), c = cos(t+uv.y*4.);
    uv *= mat2(s,c,c,-s);

    uv = fract(uv);
    uv *= sin(t/10.)*50.+60.;
    uv = fract(uv/32.)*64.;

    uv *= mat2(s,c,c,-s);

    for(float i = -4.; i < 4.; i ++)
    {
        t+=.1;
        uv += 32.*sin(i*2.);
		fragColor += sign( sin(uv.x/4.)-sin(uv.y/4.) );
    }

    fragColor = vec4(abs(sin(.15*fragColor.xyz*vec3(.4,.8,1.6)+2.*(t+uv.x/64.))),0.);
}
