vec2 tmp;
vec2 complexSquare(in vec2 coord) {
	// (x,y) -> (x^2-y^2, 2*x*y)
    tmp.xy = coord.xy*coord.xy;
    coord.y *= coord.x * 2.0;
    coord.x = tmp.x-tmp.y;
    return coord;
}


float mb(vec2 z, vec2 c, int maxIter) {
    float len;
    for(int iter = 0 ; iter < maxIter ; iter++) {
		z = complexSquare(z) + c;
        len = length(z);
        if(len > 2.0) 
            return float(iter)/float(maxIter);
    }
    return mod(len,1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x -= .25;
    uv *= 2.0;
	fragColor = vec4(uv, mb(vec2(0,0), uv, 100),1.0);
}
