void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float u;
vec4 col;
float r0 = iResolution.y / 2.  - (1.2+cos(iTime))*50.;
vec2 d = fragCoord.xy - iResolution.xy / 2.;
float r = length(d);
if(r<r0)
{
u = atan(d.y,d.x) * 15./3.14159 + iTime*4.;
col=vec4(vec3(step(.5,fract(u))),1.);
}
fragColor = col;
}
