#define F+texture(iChannel0,.3+p.xz*s/3e3)/(s+=s) 
void mainImage( out vec4 c,vec2 w)
{
    vec4 p=vec4(w/iResolution.xy,1.,1.)-.5,d=p,t;
    p.z =iTime; 
    d.y-=.6;

    for(float i=1.;i>0.;i-=.002)
    {
        float s=.4,j=1.-i;
        t = F F F;
        t = vec4(t.x*i,3.*t.y*i,t.z*j , t.w*j);
        c = d.xyzw+t;
        if(t.x>p.y*.02+1.3)break;
        p += d;
    }
}
