float letter(vec2 coord)
{
    float size = iResolution.x / 25.0;

    vec2 gp = floor(coord / size * 7.0); // global
    vec2 rp = floor(fract(coord / size) * 7.0); // repeated

    vec2 odd = fract(rp * 0.5) * 2.0;
    float rnd = fract(sin(dot(gp, vec2(12.9898, 78.233))) * 43758.5453);

    float c = max(odd.x, odd.y) * step(0.5, rnd); // random lines
    c += min(odd.x, odd.y); // corder and center points

    c *= rp.x * (6.0 - rp.x); // cropping
    c *= rp.y * (6.0 - rp.y);

    return clamp(c, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 coord = fragCoord.xy;
    coord.x += iResolution.x * 0.1 * iTime;

    float c; // MSAA with 2x2 RGSS sampling pattern
    c  = letter(coord + vec2(-3.0 / 8.0, -1.0 / 8.0));
    c += letter(coord + vec2( 1.0 / 8.0, -3.0 / 8.0));
    c += letter(coord + vec2( 3.0 / 8.0,  1.0 / 8.0));
    c += letter(coord + vec2(-1.0 / 8.0,  3.0 / 8.0));
    fragColor = vec4(c / 4.0);
}
