// turns a colored image into black and white
vec4 grayscale(vec4 texColor) 
{
    // https://en.wikipedia.org/wiki/HSL_and_HSV#Lightness
    vec3 lum = vec3(0.299, 0.587, 0.114);

    float luminance = (texColor.r + texColor.g + texColor.b) / 3.0;

    // less noise with these magic numbers of lum.rgb
    float luminance2 = texColor.r*lum.r + texColor.g*lum.g + texColor.b*lum.b;

	return vec4(vec3(luminance2), texColor.a);
}
// color modi
vec4 modi(vec4 res){
	    if(length(res.rgb) > 1.4){
    	res.rgb = vec3(sin(iTime));
    }
    else if(length(res.rgb) < 0.1){
    	res.rgb = vec3(1.0,1.0,0.0);
    }
    else if(length(res.rgb) < 0.2){
    	res.rgb = vec3(1.0,0.0,1.0);
    }
        else if(length(res.rgb) < 0.4){
    	res.rgb = vec3(0.,1.0,.5);
    }
        else if(length(res.rgb) > 1.){
    	res.rgb = vec3(0.0,1.0,1.0);
    }
    else if(length(res.rgb) > 0.6){
    	res.rgb = vec3(0.,sin(.5),.0);
    }
    return res;
}

