void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.0, 0.0, 0.0);
    if(mod(iTime, 2.0) <= 0.1 && iTime <= 326.0){
    	ret = vec3(0.5);
    }
    float max = min(163.0, iTime / 2.0);
    for(float i = max; i > 0.0; i--){
    	if(length(r - vec2(sin((iTime - i * 0.0225) * 1.71), abs(sin((iTime - i * 0.015) * 2.6)) * 2.02 - 0.95)) <= 0.05){
        	    ret=vec3(1.0, 0.0, i * (1.0 / max));
    	}
    }
    for(float i = max; i > 0.0; i--){
    	if(length(r - vec2(-0.1, 0.0) - vec2(sin(((iTime + 0.0) - i * 0.0225) * 1.71), abs(sin(((iTime + 0.0) - i * 0.015) * 2.6)) * 2.02 - 0.95)) <= 0.05){
        	    ret=vec3(i * (1.0 / max), 1.0, 0.0);
    	}
    }
    for(float i = max; i > 0.0; i--){
    	if(length(r - vec2(0.1, 0.0) - vec2(sin(((iTime + 0.0) - i * 0.0225) * 1.71), abs(sin(((iTime + 0.0) - i * 0.015) * 2.6)) * 2.02 - 0.95)) <= 0.05){
        	    ret=vec3(0.0, i * (1.0 / max), 1.0);
    	}
    }
	fragColor = vec4(ret,1.0);
}

vec2 mainSound( in int samp, float time )
{  
    if(mod(time, 2.0) <= 0.1 && time <= 326.0){
    	return vec2( sin(6.2831*440.0*time) * 2.0);
    }
    return vec2(0.0);
}
