void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//float u = 1.0;
	//fragColor = vec4(mod(fragCoord.x, u), mod(fragCoord.x, u), mod(fragCoord.x, u), 1.0);
	float q =  5.0 * (1.1 + sin(iTime));
	//q = 5.0;
	float dis = distance(iMouse.xy/iResolution.xx, fragCoord.xy/iResolution.xx)*1.0;
	float dis2 = dis;
	//dis -= distance(iMouse.xy/iResolution.xx + vec2(0.3 * sin(iTime), 0.3 * cos(iTime)), fragCoord.xy/iResolution.xy)*1.0;
	if (dis > 0.1564) {
		dis = dis * 1.4;
	};

	float is = mod(fragCoord.x, q);
	is *= abs(cos(iTime))* mod(fragCoord.y, q);
	is /= q;
	float wi = mod(fragCoord.x, q);
	wi /= abs(cos(iTime))* mod(fragCoord.y, q);
	wi /= q;
	fragColor = vec4((1.852 * dis2 * is) + ((1.0-dis2) * wi), ((dis+0.1) * is) + ((1.0-dis+0.1) * wi), (dis/1.415 * is) + ((1.0-dis) * wi), 1.0);
}
