void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float d = distance(fragCoord.xy, iResolution.xy * vec2(0.5,0.5).xy);

	float x = sin(5.0+0.1*d + iTime*-4.0) * 5.0;

	// some drivers don't appear to cope with over ranged values. 
	x = clamp( x, 0.0, 1.0 );

	fragColor = vec4( x, x, x, 1 );
}
