void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = 3.*uv-1.;	

	float c=.5,x=3.8,xx=-7.6;
	x = 3.8*sin(4.*iTime);
	xx = -2.*x;
	float s=xx*uv.x*uv.x/2.+ x*uv.x + c;
	vec3 col=vec3(0.);
	float fac = 1.-min(1.,40.*abs(s-uv.y)-.3);

	col+=vec3(0.,0.,.4)*sin(3.*uv.x+2.*uv.y-iTime);

	float sf = clamp(5.*(uv.y-.07),0.,1.);
	vec3 u=vec3(.75,.75,.6);
	col+=sf*vec3(6.,.54,.54);
	col+=sf*vec3(.9,.7,.1)*(max(1.-(length(u.xy-uv)-u.z),0.));
	float dx = max(1.-abs(uv.x-u.x),0.);
	col+=(1.-sf)*vec3(.3,.4,1.);
	col+=max(1.-sf,0.)*dx*dx;

	if (fac > 0.05)
		col = vec3(0.);

	fragColor = vec4(col,1.);

}
