void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 0.0;
    float g = 0.0;
    float b = 0.0;
	//vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 center = vec2(iResolution.x*0.5, iResolution.y*0.5);
   	vec2 centerD = vec2(sin(iTime*2.0)*iResolution.x*0.25+iResolution.x*0.5, 
                        cos(iTime*2.0)*iResolution.y*0.25+iResolution.y*0.5);


    vec2 centerD2 = vec2(iResolution.x- centerD.x, iResolution.y- centerD.y);

    float dist = distance(center, fragCoord.xy);
    float distD = distance(centerD, fragCoord.xy);
    float distD2 = distance(centerD2, fragCoord.xy);



    for(int i=1;i<15;i++){
    r += sin(float(i)*distD*0.01-iTime*1.0)*0.2-b*0.5;
    g += sin(float(i)*distD2*0.01-iTime*1.0)*0.2+r*0.5;
    b += sin(float(i)*dist*0.01-iTime*1.0)*0.2+g*0.5;
    }

    fragColor = vec4(r, g, b, 1.0);
}
