vec2 rot(vec2 s, float th)
{
	return mat2(cos(th), sin(th), -sin(th), cos(th)) * s;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * (fragCoord.xy / iResolution.xy) - 1.0;
	vec2 n = uv;

	uv = rot(uv, radians(iTime * 20.0));
	float s = texture(iChannel1, vec2(0.2,0.8)).x;
	float s2 = texture(iChannel1, vec2(0.8,0.25)).x;

	float col = 0.0;
	for(float i=0.0;i<8.0;i++) {
		uv.x += sin(s + i + iTime * 4.0 + uv.y) *
				sin(iTime * 0.5);
		col += abs((0.3*s2)/uv.x);
	}
	vec4 tex = texture(iChannel0, vec2(n.x, uv.x));
	col *= sqrt(1.25-0.5*length(n));
	fragColor = tex*col;
}
