void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5*iResolution.xy) / min(iResolution.x, iResolution.y);
    float R = 0.3;
	vec2 P = vec2(cos(iTime*2.0)/2.0, sin(iTime*2.0)/2.0);
    float len = length(uv);

	vec4 c0 = vec4(0.0);
	vec4 c1 = vec4(1.0);
	vec4 cc = mix(c0, c1, step(R, len));
	vec4 cp = mix(c0, cc, smoothstep(
	  0.5,
	  length(P) + 0.5 + (sin(iTime*2.0)/2.0),
	  1.0 - distance(uv, P)
	));

    fragColor = cp;
}
