// Start with iq's voronoi noise from https://www.shadertoy.com/view/ldl3W8
vec2 hash2( vec2 p )
{
	// texture based white noise
	return texture( iChannel0, (p+0.5)/256.0, -100.0 ).xy;

    // procedural white noise	
	//return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec3 voronoi( in vec2 x )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 mg, mr;
    vec2 col;

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
            col = o;
        }
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    md = 8.0;
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
        vec2 r = g + o - f;

        if( dot(mr-r,mr-r)>0.00001 )
        md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
    }

    return vec3( col, md );
}

vec2 rotateCircle( vec2 p, vec2 center, float angle, float inner, float outer )
{
    vec2 disp = p - center;
    float l = length( disp );
    vec2 rotX = vec2( cos( angle ), sin( angle ) );
    vec2 rotY = rotX.yx * vec2( -1.0, 1.0 );

    return ( l > outer || l < inner ) ? p : center + vec2( dot( disp, rotX ), dot( disp, rotY ) ); 
}

struct circleStruct
{
    vec2 center;
    float angle;
    float inner;
    float outer;
};

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 2.0 * (fragCoord.xy/iResolution.xx) - vec2(1.0,iResolution.y / iResolution.x  );

    const int numSplits = 5;
    vec4 circleStruct[numSplits];
    float circleRot[numSplits];

    vec2 splitCenter = vec2( 0.0 );
    vec2 splitAxis = normalize( vec2( 1.0, 0.3 ) );
    float splitSize = 0.9;

    for( int i = numSplits - 1; i >= 0; i -- )
	{   
        circleStruct[i] = vec4( splitCenter, 0.6 * splitSize, 0.8 * splitSize );
        splitSize *= 0.8;
		splitAxis = vec2( normalize( vec2(splitAxis.y, -splitAxis.x ) + 0.0 * 0.35 * hash2( 256.0 * splitCenter.xy ) ) );
        float axisDot = dot( p - splitCenter, splitAxis );
        splitCenter = vec2( splitCenter + sign( axisDot ) * splitAxis * splitSize );


    }
    for( int i = 0; i < numSplits; i++ )
	{
        p = rotateCircle( p, circleStruct[i].xy, max( 0.0, iTime - float(numSplits - 1 - i) * 5.0 ), circleStruct[i].z, circleStruct[i].w );
    }

    //p = rotateCircle( p, vec2(0.5), iTime, 0.3, 0.5 );
    //p = rotateCircle( p, vec2(0.0), iTime, 0.5, 0.7 );

    vec3 c = voronoi( 16.0*p );

	// isolines
    /*vec3 col = c.x*(0.5 + 0.5*sin(64.0*c.x))*vec3(1.0);
    // borders	
    col = mix( vec3(1.0,0.6,0.0), col, smoothstep( 0.04, 0.07, c.x ) );
    // feature points
