	col = mix( vec3(1.0,0.6,0.1), col, smoothstep( 0.0, 0.12, dd) );
	col += vec3(1.0,0.6,0.1)*(1.0-smoothstep( 0.0, 0.04, dd));*/
    vec3 col = mix( vec3( 0.2 ), vec3( hash2( c.xy * 256.0 ), 0.0 ), 1.0 - pow( 1.0 - c.z, 4.0 ) );


	fragColor = vec4(col,1.0);
}
