void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 dir   = (fragCoord.xy - iResolution.xy / 2.0);
    float dist = length(dir);
    dir       /= dist;

    fragCoord  = fragCoord.xy * 16.0 / dist;

    ivec2 resInt  = ivec2(iResolution.xy);
    ivec2 fragInt = (resInt + ivec2(fragCoord.xy) % resInt) % resInt;

    vec2 uv = vec2(fragInt) / iResolution.xy * (1.0 - 0.5 * sin(fragCoord.x / 8.0) * sin(fragCoord.y / 8.0));
    fragColor = texture(iChannel0, uv);
}
