void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 uv = (2.0*fragCoord.xy)/iResolution.y;
    float v = 0.0;	
	v+= 1.0-smoothstep(0.0,1.0,length(uv-vec2(1.0+sin(iTime*1.5),1.0)));
    v+= 1.0-smoothstep(0.0,1.0,length(uv-vec2(2.0+sin(iTime),1.0)));
	vec3 col = mix( vec3(v), vec3(sin(iTime*1.1),sin(iTime),sin(iTime*1.2)), smoothstep(0.9,0.91,v) );
	fragColor = vec4(col,1.0);
}
