#define eps 0.005
#define epsx vec2(eps, 0.0)
#define epsy vec2(0.0, eps)
#define pi 3.14152865

float depth(vec2 coord) {
    vec4 t = texture(iChannel0, coord/4.0);
    float d = t.b*0.8;
    d = mix(d, cos(coord.x*16.0)*sin(coord.y*16.0)*0.5+0.5, atan(sin((iTime*pi)/2.0))*0.5+0.5);
    return d;
}
vec2 popOut(vec2 coord) {
    vec2 c = coord;
    for (float zf=0.0; zf<=1.0; zf+=0.01) {
        float d = depth(c);
        if (zf>d) break;
        c = coord+vec2(zf*0.15);
    }
    return c;
}

vec2 normal(vec2 coord) {
    return vec2(depth(coord+epsx)-depth(coord-epsx),depth(coord+epsy)-depth(coord-epsy))/eps;
}

float shadow(vec2 coord, vec2 light) {
    vec2 n = normal(coord);
    n *= 0.05;
    float d = dot(n, n-light)*0.1+0.6;
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pop = popOut(uv);
    vec2 txy = iMouse.xy-iResolution.xy/2.0;
    txy /= min(iResolution.x,iResolution.y);
    vec3 t = texture(iChannel1, pop).rgb*shadow(pop, txy);
	fragColor = vec4(t,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 ir = vec4(
        texture(iChannel0, vec2(uv.x,0.0)).r,
        texture(iChannel0, vec2(uv.x,0.25)).r,
        texture(iChannel0, vec2(uv.x,0.5)).r,
        texture(iChannel0, vec2(uv.x,0.75)).r
    );
    if (fragCoord.y > 1.0) {
        ir = texture(iChannel1,uv-vec2(0,1.0/iResolution.y));
    }
    fragColor = ir;
}
