void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv_shifted = vec2(uv.x, uv.y + iTime * 0.25);


    vec3 camera = texture( iChannel0, uv).xyz;

    vec3 camera1 = texture( iChannel0, vec2(0.25, 0.25)).xyz;
    vec3 camera2 = texture( iChannel0, vec2(0.75, 0.25)).xyz;
    vec3 camera3 = texture( iChannel0, vec2(0.25, 0.75)).xyz;
    vec3 camera4 = texture( iChannel0, vec2(0.75, 0.75)).xyz;

    vec3 shit = texture( iChannel1, uv_shifted ).xyz;

    float intencity1 = dot(camera1, vec3(0.3333,0.3333,0.3333));
    float intencity2 = dot(camera2, vec3(0.3333,0.3333,0.3333));
    float intencity3 = dot(camera3, vec3(0.3333,0.3333,0.3333));
    float intencity4 = dot(camera4, vec3(0.3333,0.3333,0.3333));

    float intencity_camera = dot(camera, vec3(0.3333,0.3333,0.3333));
    float intencity = intencity1 * 0.25 + intencity2 * 0.25 + intencity3 * 0.25 + intencity4 * 0.25;

    intencity = clamp(intencity, 0.0001, 1.0);

    float mask = 1.2 * intencity_camera / intencity; 

    mask = clamp(mask, 0.0, 1.0);

    fragColor = vec4(mix(shit, camera, mask),1.0);
    //fragColor = vec4(intencity, intencity, intencity, 1.0);
}
