void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float bpm = 1.0 / 60.0 * 86.1 * 3.1415926535 * 2.0;
	vec2 uv = fragCoord.xy / iResolution.xy;
  	vec2 nuv = uv * 2.0 - 1.0;
    float t = iTime;
    float mt = t * bpm;
    float mt0 = mt + 3.0;
    float mt1 = mt0; 
    float pi = 3.1415926535;

    vec2 x0 = abs(nuv * nuv) * 1.0 + bpm * t / 4.0;
    vec2 x1 = x0 * vec2(cos(mt0 / 4.0), sin(mt0 / 4.0));
    float x = fract(length(x1));
    x = pow(x, 8.0);

    vec2 y0 = abs(nuv * nuv) * 1.0 + bpm * t / 4.0;
    vec2 y1 = y0 * vec2(sin(mt1 / 2.0), cos(mt1 / 2.0));
    float y = fract(length(y1));
    y = pow(y, 7.0);

    float hum = texture(iChannel0, vec2(0.04, 0.25)).x;

    hum = pow(hum, 3.0) * 4.5;

    float bell = texture(iChannel0, vec2(0.10, 0.25)).x;

    bell = pow(bell, 2.0) * 8.0;

    vec2 ns = texture(iChannel1, nuv * 4.0).xy;

    float c0 = x * bell;
    float c1 = y * bell * length(ns);
    float c2 = hum * sin(mt / 8.0);

    vec3 col0 = vec3(0.0, 1.0, 0.8);
    vec3 col1 = vec3(0.0, 0.6, 1.0);
    vec3 col2 = vec3(0.3, 0.3, 0.3);

    c0 = abs(c0);
    c1 = abs(c1);
    c2 = abs(c2);

    fragColor.xyz = 
        pow(c0, 0.9) * col0 + col0 * 0.01 +
        pow(c1, 0.3) * col1 + col1 * 0.05;

    fragColor.xyz += col2 * hum + col2 * 0.05 * c1;
}
