void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float frequency = 50.0;
	float amplitude = 0.005;
    float speed = 3.0;
	float distortion = sin(uv.y*frequency+iTime*speed)*amplitude;

    vec4 tex2 = texture(iChannel1,uv);


    if(tex2.r > 0.2 || tex2.b > 0.2)
    {
        float ratio = iTime*0.001;
        ratio *= ratio;
        ratio = sin(ratio);
        uv.x += distortion * ratio;
        uv.y += distortion * (1.0 - ratio);
    }

    vec4 tex = texture(iChannel0,uv);
	fragColor = tex;
}
