void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;

    vec4 texColor = texture(iChannel0, xy);
    vec4 tex2Color = texture(iChannel1, xy);
    /*
    if ((texColor.r+texColor.b)<texColor.g){
     texColor.rgb = tex2Color.rgb;
    }*/
    vec4 backgroundColor = vec4(13.0/255.0,161.0/255.0,37.0/255.0,1.0);
	float difference = distance(texColor,backgroundColor); //calculates the difference between two vectors
	if( difference < 0.4 ) {

		texColor.rgb = tex2Color.rgb;

	}

    fragColor = texColor;

    //texColor.rgb *= abs(sin(iTime)); //fade in and out to black

    //fragColor = texColor;
}
