const int height=5;
float bars=60.;
float textureIn(vec2 uv){
    uv.y+=float(height)/(1.5*bars);
	return(texture(iChannel0,uv).r+texture(iChannel0,uv).g+texture(iChannel0,uv).b)/3.;
}
float draw(vec2 uv){
	float thickness=0.1;
	//return abs(textureIn(vec2(uv.x,floor(uv.y*bars)/bars))-fract(uv.y*bars))<0.1?1.:0.;
	float o=0.;
	for(int i=0;i<height;i++){
		o+=abs((textureIn(vec2(uv.x,(floor(uv.y*bars)-float(i))/bars))*float(height))-(fract(uv.y*bars)+float(i)))<thickness?1.:0.;
	}
	return o;
}
void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=fragCoord.xy/iResolution.xy;
	float o=0.;
	for(int x=-2;x<=2;x++){
		for(int y=-2;y<=2;y++){
			o+=draw(uv+(vec2(x,y)/(iResolution.xy*5.)));
		}
	}
	o/=25.;
	fragColor=vec4(mix(vec3(0.1),vec3(1.,uv.y,.5-uv.y),o),1.);
}
