float A = 0.22;
float B = 0.30;
float C = 0.10;
float D = 0.20;
float E = 0.01;
float F = 0.30;
float LinearWhitePointValue = 11.2;
float exposure = 2.0;

vec3 toneMapping(vec3 x){
	return ((x*(A*x+C*B)+D*E)/(x*(A*x+B)+D*F))-E/F;
}

float getWhite(){
	return ((LinearWhitePointValue*(A*LinearWhitePointValue+C*B)+D*E)/(LinearWhitePointValue*(A*LinearWhitePointValue+B)+D*F))-E/F;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 texColor = texture(iChannel0,uv).rgb;

    texColor = texColor * exposure;
    vec3 finalColor = toneMapping(texColor);

    float linearWhite = getWhite();
    finalColor = finalColor / linearWhite;

    texColor = pow(texColor, vec3(1.0 / 2.2));


    fragColor = vec4(finalColor.xyz,1.0);

}
