void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 c = vec3(0);
    vec3 A = texture(iChannel0, uv).rgb;
    vec3 B = texture(iChannel1, uv).rgb;
    c.x+=A.x;
    c.z+=B.z;
    c.g+=B.g;
	fragColor = pow(vec4(c,1.0),vec4(1./2.2));
}

vec2 map ( vec3 o ) {
    float planar = o.y+.1;
    float phonar = length(max(abs(o)-vec3(.8,.1,1.8),0.0))-.1;
    float a = planar;
    a=min(planar,phonar);
    float b = a==planar?0.:1.;
    return vec2(a,b);
}
float trace( vec3 o , vec3 d, float minD ) {
	float t = minD;
    for (int i = 0; i < 64; ++i) {
    	t+=map(o+d*t).x;    
    }
    return t;
}
vec3 normals( in vec3 x )
{
    vec2 e = vec2( .01, 0.0 );
    return normalize( vec3( map(x+e.xyy).x - map(x-e.xyy).x,
                            map(x+e.yxy).x - map(x-e.yxy).x,
                            map(x+e.yyx).x - map(x-e.yyx).x ) );
}
vec3 light = vec3(2, 1, 0);
vec3 shade ( vec3 o , vec3 i) {
    vec3 c = vec3(0);  
      vec3 n = normals(o);
    float dv=dot(n,normalize(light-o))/distance(o,light);
    float mat = map(o).y;
    if (mat==0.) {
        c=texture(iChannel1,o.xz/10.).rgb*dv;
    } else if (mat==1.) {

    	vec2 uv = (o.xz+vec2(1,2))/2.;
        vec3 body = vec3(50./256.);

        body*=dv;
        float spec = max(pow(dot(reflect(i,n),normalize(light-o)),50.),0.0);
        if (o.y>.09) {
       		c=texture(iChannel0,uv.yx/vec2(2,1)).rgb*dv+max(abs(spec)/1.5,0.0);

            if (uv.y<.15||uv.y>2.-.15) {
            	c=body;   
            }
        } else {
            vec2 qv = o.xy;
            c = body;
            if (o.z<-.95)
            {
            	float dv = .125;
                if (abs(qv.x)<dv&&abs(qv.y)<dv/4.) 
                {
                	c=vec3(.5);
                }
            }
        }
    }
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 o = vec3(-5., .5+iMouse.y/iResolution.y*3., -3.*cos(iMouse.x/iResolution.y*acos(-1.)/2.)-.1);
    vec3 d = vec3(uv-.5,.5);
    d=d.zyx;
    d.x*=iResolution.x/iResolution.y;
    d=normalize(d);
    vec3 c = vec3(0);
    float t = trace(o,d,0.0);
    vec3 hit = o+d*t;

    vec2 hd = map(hit);
    if (hd.x<.025) {
       c=shade(hit,d);
    }
    c+=pow(dot(d,normalize(light-o)),5000.)*3.;
	fragColor = vec4(c,1.0);
}

