const float lineWidth = 0.21;
const float factor = 1.0;
const float speed = 0.2;

float pointToLine( vec2 pos, vec2 lineDir, vec2 linePoint ) {
 	lineDir = normalize( lineDir );
    vec2 pointDir = pos - linePoint;
    float s1 = dot( lineDir, pointDir );
    return sqrt( dot( pointDir, pointDir ) - s1 * s1 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float dis = pointToLine( uv, vec2( 1.0, 1.0 ), vec2( 0.0, 1.0 - 2.0 * mod( iTime * speed , 1.0 ) ) );
    float a = max( 1.0 - dis / lineWidth, 0.0 );

    vec4 color0 = texture( iChannel0, uv );
    vec4 color1 = texture( iChannel1, uv );
    float alpha = color0.a * color1.a;
	fragColor = color0 + color1 * alpha * a * factor;
}

