void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(248.0/255.0,241.0/255.0,225.0/255.0);

    // fill red
    if (st.x<0.21 && st.y>0.63) {
       color = vec3(168.0/255.0,34.0/255.0,35.0/255.0);
    }

    // fill yellow
    if (st.x>0.955 && st.y>0.63) {
       color = vec3(254.0/255.0,195.0/255.0,45.0/255.0);
    }

    // fill blue
    if (st.x>0.73 && st.y<0.06) {
       color = vec3(0.0,93.0/255.0,151.0/255.0);
    }

    // draw line
    if (st.y>0.06 && st.y<0.09 && st.x>0.21) {
       color = vec3(0.0);
    }
    if (st.y>0.63 && st.y<0.66) {
       color = vec3(0.0);
    }
    if (st.y>0.81 && st.y<0.84) {
       color = vec3(0.0);
    }
    if (st.x>0.06 && st.x<0.075 && st.y>0.63) {
       color = vec3(0.0);
    }
    if (st.x>0.21 && st.x<0.225) {
       color = vec3(0.0);
    }
    if (st.x>0.73 && st.x<0.745) {
       color = vec3(0.0);
    }
    if (st.x>0.94 && st.x<0.955) {
       color = vec3(0.0);
    }

    fragColor = vec4(color,1.0);
}
