vec3 COLOR1 = vec3(0.0, 1.0, 2.0);
vec3 COLOR2 = vec3(0.0, 0.0, 10.1);
float BLOCK_WIDTH = 0.005;
float time1 = 12.5;
float time2 = 20.0;
float time3 = 30.0;
float time4 = 40.0;
float time5 = 50.0;
float time6 = 58.0;
float time7 = 65.0;
float time8 = 80.0;
float time9 = 82.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 final_color = vec3(1.0);
	vec3 bg_color = vec3(0.0);
	vec3 bar_color = vec3(0.0);

	float c1 = mod(uv.x, 3.0 * BLOCK_WIDTH);
	c1 = step(BLOCK_WIDTH, c1);

	float c2 = mod(uv.y, 2.0 * BLOCK_WIDTH);
	c2 = step(BLOCK_WIDTH, c2);

	bg_color = mix(uv.x * COLOR1, uv.y * COLOR2, c1 * c2);

	for(float i = 0.0; i < 15.0; i++) {

		if (uv.x > 50.0) {
			uv.y += (0.09 * asin(uv.x + i/7.0 + iTime ));		
		} else if (iTime < time1){
			uv.y += (0.5 * tan(uv.x + i/50.0 + iTime ));
		} else if (iTime > time1 && (iTime < time2 || iTime > time9)) {
			uv.y += (0.07 * tan(uv.y + uv.x + i/500.0 + iTime ));
		} else if (iTime > time3 && iTime < time9) {
			uv.y += (1.0 * sqrt(uv.y + uv.x + i/5000.0 + iTime ));
			uv.y += (0.1 * cos(uv.y + uv.x + i/5000.0 + iTime ));
		} else if (iTime > time7 && iTime < time9) {
			uv.y += (1.0 * sqrt(uv.y + uv.x + i/5000.0 + iTime ));
			uv.y += (0.1 * tan(uv.y + uv.x + i/5000.0 + iTime ));
		} else {
			uv.y += (0.05 * tan(uv.x + i/500.0 + iTime ));
		}

		if (uv.y > 60.0 && iTime < time1) {
			bar_color = vec3(0.0, 0.0, atan(uv.x * iTime / 3.0));
		} else if (uv.y > 59.0 && iTime < time3) {
			bar_color += vec3(0.0, 0.0, uv.y / 2.0);
		} else if (uv.y > 60.0 && iTime > time3) {
			bar_color += vec3(0.0, 0.0, uv.y / 500.0);
		}
	}

	final_color = bg_color + bar_color;

	vec4 _texture = vec4(1.0);
	if (iTime < time1) { 
		_texture = texture(iChannel0, uv);
	} else if (iTime >= time1 && iTime < time2) {
		_texture = texture(iChannel1, uv);
	} else if (iTime > time2 && iTime < time3) {
		_texture = texture(iChannel2, uv);
	} else if (iTime >= time3 && iTime < time4) {
		_texture = texture(iChannel3, uv);		
		vec2 texture2 = texture(iChannel3, uv + sin(iTime) * 0.1).rg - 0.5;
		_texture = texture(iChannel3, uv + texture2 * 0.1);
	} else if (iTime >= time4 && iTime < time5) {
		_texture = texture(iChannel3, uv);		
		vec2 texture2 = texture(iChannel3, uv + sin(iTime) * 0.5).rg - 0.5;
		_texture = texture(iChannel3, uv + texture2 * 0.1);
	} else if (iTime >= time5 && iTime < time6) {
		_texture = texture(iChannel3, uv);				
	} else if (iTime >= time6 && iTime < time7) {
		_texture = texture(iChannel3, uv);		
		vec2 texture2 = texture(iChannel3, uv * sin(iTime) * 0.1).rg - 0.5;
		_texture = texture(iChannel3, uv + texture2 * 0.1);
	} else if (iTime >= time7 && iTime < time8) {
		_texture = texture(iChannel1, uv);
		vec2 texture2 = texture(iChannel1, uv + sin(iTime) * 0.01).rg - 0.5;
		_texture = texture(iChannel1, uv + texture2 * 0.1);

		vec2 texture3 = texture(iChannel0, uv + cos(iTime) * 0.01).rg - 0.01;
		_texture = texture(iChannel3,uv + texture2 + texture3 * 0.01);
	} else if (iTime >= time8) {
		_texture = texture(iChannel3, uv);		
		vec2 texture2 = texture(iChannel3, uv * sin(iTime) * 0.1).rg - 0.5;
		_texture = texture(iChannel3, uv + texture2 * 0.1);
	}

	fragColor = vec4(final_color * _texture.y * _texture.x, 1.0);
}
