float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
mat2 rotate(float Angle)
{
    mat2 rotation = mat2(
        vec2( cos(Angle),  sin(Angle)),
        vec2(-sin(Angle),  cos(Angle))
    );
	return rotation;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * uv - 1.0;
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;
	coord*=0.01+0.15/mod((iTime)*1.,1.0);
	coord*=rotate(iTime*3.141*2.*(-.5+mod(floor(iTime*0.5),2.0)));
	vec4 o=vec4(vec3(0.),1.);
	if(coord.x>-.5 && coord.x<.5 && coord.y>-.05 && coord.y<.05){
		coord.y=1.-coord.y;
		coord.x-=0.5;
		coord.y-=0.5;
		coord.y+=floor(iTime)*(1./8.)+(1./16.);
		o=texture(iChannel0,coord);
	}
	fragColor = o;
}
