float rand2(vec2 co,float seed)
{
	return fract(sin(dot(co.xy ,vec2(seed,78.233))) * 43758.5453);
}

float rand(float n,float seed)
{
	return fract(sin(n*4532.63264)*5375.52465 * seed);
}

float CosInterpolate(float v1, float v2, float a)
{
	float angle = a * 3.14159;
	float prc = (1. - cos(angle)) * 0.5;
	return  v1*(1.0 - prc) + v2*prc;
}

float noise(float pos,float Size,float seed)
{	
	float grid = floor(pos * Size) * 0.1;
	float PosGrid = mod((pos),1./Size) * Size ;
	float nextGrid =  floor((pos + (1./Size))* Size) * 0.1;
	float sample1 = ((rand(grid,seed)));
	float sample2 = ((rand(nextGrid,seed)));

	//return nextGrid;
	return CosInterpolate(sample1,sample2,PosGrid);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 uv = vec3(fragCoord.xy / iResolution.xy , .15);		
	float u = noise(uv.x + (iTime * 0.1),110. / (10. + (iMouse.x/30.)),272.);
	u *= noise(uv.y + (iTime * 0.1),10.,272.) ;
	u += noise(uv.y + (iTime * 0.1),10.,272.) ;

	float v = noise(uv.y + (u * 0.1),110.,272.);


	float Val  = u * v;
	vec3 ColorA = vec3(0.15,0.13,0.134) * 2.;
	ColorA *= ColorA;

	vec3 ColorB = vec3(0.6,0.25,0.1354) * 1.2;
	ColorB *= ColorB;



	vec3 Wood = mix(ColorA,ColorB,Val);


	fragColor = vec4(Wood,1. )  ;	
}
