// Sun Shield - Jim Bumgardner

const float PI = 3.1415926535897932384626433832795;
const float PId2 = PI/2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

  vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
  p.y *= iResolution.y/iResolution.x;

  float a = atan(p.x, p.y)+iTime*.001;
  float d = length(p)*6.0;
  fragColor = vec4(
	                  mix(
	                   vec3(.5*sin(vec3(iTime*.999,iTime,-iTime*.666)+a*pow(2.0,1.0+ceil(d)))),
	                   vec3(1.0+.5*sin(vec3(iTime*.555,-iTime,iTime*1.111)+a*pow(2.0,1.0+ceil(d+sin(iTime*0.1))))), 0.5),

                      1.0);

}
