void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float radius =100.0;

	vec2 mouseCoords = vec2(iMouse.xy);

    if (mouseCoords.x == 0.0)
        mouseCoords = vec2(iResolution.x/2.0,mouseCoords.y);
    if (mouseCoords.y == 0.0)
        mouseCoords = vec2(mouseCoords.x,iResolution.y/2.0);

    float distan = distance(mouseCoords,fragCoord.xy);
	float intensity = ((radius-distan)/radius);

	float offset = (sin(iTime)+1.0)/2.0;
	float final = ((offset+intensity)/2.0);

	fragColor = vec4(0.0,0.0,final,1.0);
}
