void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = vec4(0);

    //Geometric parameters
    float scale= iResolution.y /2.;
    vec2 center= fragCoord.xy -iResolution.xy /2.; 
    vec2 pos;
    float radius= scale /10.;
    float width= 40.;
    float c; //closeness

    //coloring parameters
    float p= 1.; //2.
    float a= .05; //width
    float b= .0;
    float d= 40.;

    //time parameters
    float rate= .2;
    float amp= scale/1.25;

    //Blue Pattern
    radius *= .8;
    pos= center - vec2( amp, 0.) *sin(iTime *rate); 
    c= width -abs(( sin( pos.x /radius)+ sin( pos.y /radius) -sin( iTime *rate) *2.5) *d);
    fragColor += vec4( vec2(0.), pow(c,p)*a +b, 0.);

    //Red Pattern
    radius *= .8;
    pos= center - vec2( amp, 0.) *sin( iTime *rate *.8);
    c= width -abs(( sin( pos.x /radius)+ sin( pos.y /radius) -sin( iTime *rate) *2.5) *d);
    fragColor += vec4( pow(c,p)*a +b, vec3(0.));

    //Green Pattern
    radius *= .8;
    pos= center - vec2( amp, 0.) *sin(iTime *rate *1.25);
    c= width -abs(( sin( pos.x /radius)+ sin( pos.y /radius) -sin( iTime *rate) *2.5) *d);
    fragColor += vec4( 0., pow(c,p)*a +b, vec2(0.));
}
