#define PI 3.14159265359
#define TWO_PI 6.28318530718

#define pick 2

mat2 scale(float size)
{
    return mat2(size,0,0,size);
}

vec2 multScreen (vec2 uv , float value)
{
    uv*=value;
    return fract(uv);
}

mat2 rotation(float angle)
{
    float rot = radians(angle);
	return mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
}

vec3 createShape(vec2 uv, float sides, vec2 pos, vec3 color, float angle, float size, float thickness)
{
    uv = uv *2.-1.2;
    uv /= size;
    uv*=rotation(angle);
    uv.x+=pos.x;
    uv.y+=pos.y;

    float a = atan(uv.x,uv.y)+PI;
  	float r = TWO_PI/float(sides);
    float dist = cos(floor(.5+a/r)*r-a)*length(uv);

    return vec3(step(thickness,dist)-step(1.,dist)) * color;
}

vec3 rectangle(vec2 uv , vec2 position , vec2 size , vec3 color)
{
    float xBorder = position.x + size.x;
    float yBorder = position.y + size.y;

    float hor = step(0.0,uv.x-position.x)-step(0.0,uv.x-xBorder);
    float ver = step(0.0,uv.y-position.y)-step(0.0,uv.y-yBorder);

    return (ver * hor) * color;
}

vec3 verticalStripe(vec2 uv, float start, float end , vec3 color)
{
    return (step(start,uv.x)-step(end,uv.x))*color;
}

vec3 horizontalStripe(vec2 uv, float start, float end , vec3 color)
{
    return (step(start,uv.y)-step(end,uv.y))*color;
}


vec3 createCircle(vec2 uv, vec2 position,float radius,vec3 color)
{
    uv += position;
  	float pos =  (distance(uv,vec2(0.4)),distance(uv,vec2(0.6)));
  	return step(pos,radius) * color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    uv.x*= ratio;
    vec3 frag = vec3(0,0,0);

    //Patern 1
    if (pick == 1)
    {
    uv = multScreen(uv,5.);
    uv.x+=0.4;
    vec3 verStripe =horizontalStripe(uv,0.45,0.55,vec3(0.400,0.400,0.400));
    vec3 horStripe =verticalStripe(uv,0.85,0.95,vec3(0.400,0.400,0.400));
    frag = (verStripe + horStripe);
    }

    //Patern 2
    else if (pick == 2)
    {
    uv = multScreen(uv,12.);
    uv.x+=-.5;
    uv.y-=0.5;
    uv*=rotation(100.*iTime)*5.;
    vec3 horStripe =horizontalStripe(uv,0.45,0.55,vec3(cos(iTime*10.)*0.455,cos(iTime*7.)*0.200,cos(iTime*12.)*0.900));
    vec3 verStripe =verticalStripe(uv,0.85,0.95,vec3(sin(iTime*10.)*0.455,sin(iTime*7.)*0.200,sin(iTime*12.)*0.900));
    frag = verStripe+horStripe;
    }

    //Patern 3
    else if (pick == 3)
    {
    uv*=multScreen(uv,5.0);
    uv.x+=0.3;
    float angle = (iTime/3.) + atan(uv.y,uv.x);
    float radius = (exp(abs(cos(angle * 26.) * sin (angle * 12.)) * .3+ .2/10.)/2.);
    vec3 circle = createCircle(uv,vec2(-0.3,0.1),radius,vec3(1,1,1));
    frag = circle;
    }

    //patern 4
    else if (pick == 4)
    {
    uv=multScreen(uv,5.);
    uv.x+=0.75;
    uv.y+=0.50;
    float rand1 = (abs(sin(iTime/2.)))*16.;
    float rand2 = fract(cos(iTime/3.5))*9.;
    float rand3 = abs(log(tan(iTime/2.5)))/2.;
    float rand4 = ceil(acos(iTime/2.));
    vec3 rect1 = rectangle(uv,vec2(rand1*0.1/2.,rand2*0.1/2.),vec2(0.3,0.3),vec3(0.300,0.0,0.0));
    vec3 rect2 = rectangle(uv,vec2(rand3*0.8,0.2),vec2(0.5,0.3),vec3(0.0,0.300,0.0));
    vec3 rect3 = rectangle(uv,vec2(0.7,rand1/10.*0.6),vec2(0.3,0.2),vec3(0.0,0.0,0.300));
    vec3 rect4 = rectangle(uv,vec2(rand1*rand2*0.01,0.7),vec2(0.6,0.2),vec3(0.300,0.300,0.0));
    vec3 rect5 = rectangle(uv,vec2(0.9,0.7*sin(iTime/2.)),vec2(0.6,0.2),vec3(0.0,0.300,0.0));
    vec3 rect6 = rectangle(uv,vec2(0.0,0.0),vec2(2.0*abs(sin(iTime)),2.6*abs(sin(iTime))),vec3(0.300,0.300,0.300));
    vec3 rect7 = rectangle(uv,vec2(rand1*0.5/3.,rand2*0.2/3.),vec2(0.3,0.3),vec3(0.300,0.0,0.300));
    frag = rect1+rect2+rect3+rect4+rect5+rect6+rect7;
    }

    //patern 5
    else if (pick == 5)
    {
    uv=multScreen(uv,10.);
    uv.x+=0.50;
    vec3 stripe1 = horizontalStripe(uv, 0.45,0.50 , vec3(0.,0.300,0.));
    vec3 stripe2 = verticalStripe(uv,0.90,0.95,vec3(0.,0.300,0.300));
    vec3 stripe3 = horizontalStripe(uv*rotation(45.)-0.515, 0.45,0.50 , vec3(0.,0.,0.300));
    vec3 stripe4 = horizontalStripe(uv*rotation(135.)+0.155, 0.45,0.50 , vec3(0.300,0.,0.));
    vec3 circle = createCircle(uv,vec2(-0.32,0.12),0.3,vec3(sin(iTime)*0.300,cos(iTime)*0.300,cos(iTime)*0.400));
    frag = stripe1+stripe2+stripe3+stripe4+circle;
    }

    //patern 6
    else if (pick == 6)
    {
    uv =multScreen(uv,4.);
    vec3 stripe1 = verticalStripe(uv,0.90,0.95,vec3(0.,0.,0.400));
    vec3 stripe2 = verticalStripe(uv,1.0,1.05,vec3(0.,0.400,0.));
    vec3 stripe3 = verticalStripe(uv,1.10,1.15,vec3(0.400,0.,0.));
    vec3 stripe4 = verticalStripe(uv,1.20,1.25,vec3(0.,0.,0.400));
    vec3 stripe5 = verticalStripe(uv,1.30,1.35,vec3(0.,0.400,0.));
    vec3 stripe6 = verticalStripe(uv,1.40,1.45,vec3(0.,0.,0.400));
    vec3 stripe7 = verticalStripe(uv,1.50,1.55,vec3(0.400,0.,0.));
    vec3 stripe8 = verticalStripe(uv,1.60,1.65,vec3(0.,0.400,0.));
    vec3 stripe9 = verticalStripe(uv,1.70,1.75,vec3(0.,0.,0.400));
    vec3 stripe10 = verticalStripe(uv,0.80,0.85,vec3(0.400,0.400,0.));
    vec3 stripe11 = verticalStripe(uv,0.70,0.75,vec3(0.,0.400,0.400));
    vec3 stripe12 = verticalStripe(uv,0.60,0.65,vec3(0.400,0.,0.400));
    vec3 stripe13 = verticalStripe(uv,0.50,0.55,vec3(0.400,0.400,0.));
    vec3 stripe14 = verticalStripe(uv,0.40,0.45,vec3(0.400,0.,0.400));
    vec3 stripe15 = verticalStripe(uv,0.30,0.35,vec3(0.,0.400,0.400));
    vec3 stripe16 = verticalStripe(uv,0.20,0.25,vec3(0.400,0.400,0.));
    vec3 stripe17 = verticalStripe(uv,0.10,0.15,vec3(0.400,0.,0.400));
    vec3 stripe18 = verticalStripe(uv,0.0,0.05,vec3(0.360,0.250,0.100));
    vec3 stripe19 = verticalStripe(uv,abs(0.0+sin(iTime/2.))*2.,abs(0.05+sin(iTime/2.))*2.,vec3(0.360,0.250,0.100));
    frag = stripe1+stripe2+stripe3+stripe4+stripe5+stripe6+stripe7+stripe8+stripe9+stripe10+stripe11+stripe12+stripe13+stripe14
        +stripe15+stripe16+stripe17+stripe18+stripe19;
    }

    //patern 7
    else if (pick == 7)
    {
    uv=multScreen(uv,12.0);
    uv*=rotation(sin(iTime)*45.);
    vec2 shapePos = vec2(.5*ratio , .5);
    float dist = distance(shapePos,uv)*2.0;
    uv-= shapePos;
    float angle = (iTime) + atan(uv.y,uv.x);
    float radius = sin(3.0*angle);
    radius = sin(angle*74.0)*sin(angle*40.0)*sin(angle * 8.0);
    radius = sqrt(exp(abs(cos(angle * 26.) * sin (angle * 12.))) * .3+ .2/10.);
    float value = 1.-step(radius, dist);
    frag = value * abs(vec3(sin(iTime)*0.400,cos(iTime)*0.400,cos(iTime)*0.400));
    }

    //patern 8
    else if (pick == 8)
    {
    uv = multScreen(uv,3.);
    vec3 stripe1 = verticalStripe(uv*rotation(45.),-0.60,-0.50 , vec3(0.400,0.400,0.400));
    vec3 stripe2 = verticalStripe(uv*rotation(45.),-0.40,-0.30 , vec3(0.400,0.400,0.400));
    vec3 stripe3 = verticalStripe(uv*rotation(45.),-0.20,-0.10 , vec3(0.400,0.400,0.400));
    vec3 stripe4 = verticalStripe(uv*rotation(45.),0.00,0.10 , vec3(0.400,0.400,0.400));
    vec3 stripe5 = verticalStripe(uv*rotation(45.),0.20,0.30 , vec3(0.400,0.400,0.400));
    vec3 stripe6 = verticalStripe(uv*rotation(45.),0.40,0.50 , vec3(0.400,0.400,0.400));
    vec3 stripe7 = verticalStripe(uv*rotation(45.),0.60,0.70 , vec3(0.400,0.400,0.400));
    vec3 stripe8 = verticalStripe(uv*rotation(45.),0.80,0.90 , vec3(0.400,0.400,0.400));
    vec3 stripe9 = verticalStripe(uv*rotation(45.),1.00,1.10 , vec3(0.400,0.400,0.400));
    vec3 stripe10 = horizontalStripe(uv,0.0,0.05,vec3(0.400,0.400,0.400));
    vec3 stripe11 = horizontalStripe(uv,0.10,0.15,vec3(0.400,0.400,0.400));
    vec3 stripe12 = horizontalStripe(uv,0.20,0.25,vec3(0.400,0.400,0.400));
    vec3 stripe13 = horizontalStripe(uv,0.30,0.35,vec3(0.400,0.400,0.400));
    vec3 stripe14 = horizontalStripe(uv,0.40,0.45,vec3(0.400,0.400,0.400));
    vec3 stripe15 = horizontalStripe(uv,0.50,0.55,vec3(0.400,0.400,0.400));
    vec3 stripe16 = horizontalStripe(uv,0.60,0.65,vec3(0.400,0.400,0.400));
    vec3 stripe17 = horizontalStripe(uv,0.70,0.75,vec3(0.400,0.400,0.400));
    vec3 stripe18 = horizontalStripe(uv,0.80,0.85,vec3(0.400,0.400,0.400));
    vec3 stripe19 = horizontalStripe(uv,0.90,0.95,vec3(0.400,0.400,0.400));
    vec3 stripe20 = horizontalStripe(uv,1.0,1.05,vec3(0.400,0.400,0.400));
    frag = stripe1+stripe2+stripe3+stripe4+stripe5+stripe6+stripe7+stripe8+stripe9+stripe10+stripe11+stripe12+stripe13+stripe14
        +stripe15+stripe16+stripe17+stripe18+stripe19+stripe20;
    }

	fragColor = vec4(vec3(frag),1.0);
}
