void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy; //create NDC

    vec4 texColor0 = texture2D(iChannel0, xy);

    vec4 texColor1 = texture2D(iChannel1, xy);

    if(texColor0.r + texColor0.b < texColor0.g)
    {
        fragColor = texColor1;
    }
    else
    {
        fragColor = texColor0;
    }
}
