#define PI 3.14159;

float circ(vec2 p) {
	float r = length(p);
    r = log(sqrt(r));

    return abs(mod(4. * r, 6.) - 3.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
   	vec3 color = vec3(.0);

    float rz = 1.;
    uv /= exp(mod(iTime * .15, 3.));

    rz *= abs(circ(uv));

    color = vec3(.2, .1, .6) / rz;

	fragColor = vec4(color, 1.0);
}
