// Illusion's Parallx
// This is a simple parallax scroller. For some people, it creates the optical illusion
// to be 'real' 3d, because of the color-selection (red gradient on blue background).
// Thanks to AndrÃ© Hugenroth for discovering this and for the idea!
// 
// Created by Frank Hugenroth 06/2017
//
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//

#define PARALLAX_RATIO 0.05;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pixel = fragCoord.xy - iResolution.xy*.5;
	const float pixelSize = 4.0;
	pixel = pixel/pixelSize;
	vec2 offset = vec2(iTime*3000.0,pow(max(-sin(iTime*.2),.0),2.0)*16000.0)/pixelSize;
    if (iMouse.z > 0.0)
        offset = -iMouse.xy*10.;
	vec3 col;
	for ( int i=0; i < 32; i++ )
	{
		// parallax position
		float depth = 20.0+float(i)*PARALLAX_RATIO;
		vec2 uv = pixel + offset/depth;
		uv /= iResolution.y;
		uv *= depth/10.0;
		uv *= .5*pixelSize;
		col = texture( iChannel0, 0.8*uv+.5 ).rgb * 1.4 + texture( iChannel1, uv+.5 ).rgb * .4 - 0.1;
		if ( 1.0-col.r < float(i+1)/32.0 )
		{
			col = mix( vec3(1.6,.0,.0), col, exp2(-float(i)*.0025) );
			break;
		}
	}
    col.g = 0.;
    col.b = 0.;
  	if (col.r < .2)
      col = vec3(0.,0.,1.);
    col.r -= 0.2;
	fragColor = vec4(col,1.0);
}
