void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec3 baggrund = vec3(0.,128.,0.)/255.;

    vec3 blaa = vec3(0.,0.,255.)/255.;

    vec3 roed = vec3(255.0,0.0,0.0)/255.;

    vec3 brun = vec3(0.0,0.0,0.0)/255.;

    vec3 pixel = baggrund;



    if (fragCoord.x > 10. 
    &&  fragCoord.x < 30. 
    &&  fragCoord.y > 10. 
    &&  fragCoord.y < 150.) {
        pixel = blaa;
    }    

    if (fragCoord.x > 10. 
    &&  fragCoord.x < 40. 
    &&  fragCoord.y > 150. 
    &&  fragCoord.y < 270.) {
        pixel = roed;

    }

    fragColor = vec4(pixel,1.0);

}
