void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float t = iTime;
    float bpm = 1.0 / 60.0 * 119.9;
    float pi = 3.1415926535;
    float bpmt = t * bpm * pi / 2.0;
    float beat = sin(t * bpm * pi / 2.0);

    float drum = texture(iChannel0, vec2(0.0, 0.25)).x;
    float chime = texture(iChannel0, vec2(0.05, 0.25)).x;

    nuv = vec2(
        nuv.y * 1.5 + abs(cos(bpmt * nuv.y)) * 0.05 * sin(uv.x * 120.0 + 2000.0 * drum),
        nuv.x * 0.5 + abs(sin(bpmt * nuv.y)) * 2.25 * sin(uv.x * 100.0 * drum)
    );

    beat = abs(beat);
    beat = pow(beat, 24.0);

    float wave = texture(iChannel0, vec2(abs(nuv.x), 0.25)).x;

    vec2 nuv0 = vec2(abs(nuv.x) * wave, abs(nuv.y) * wave);
    float wave0 = texture(iChannel0, vec2(abs(nuv0.x), 0.25)).x;

    beat *= pow(drum, 0.5) * 0.5;
    wave = pow(wave, 15.0) * 10.0;

    vec3 beatcol = vec3(0,0,1) * beat;

    fragColor.y = chime * wave;
    fragColor.z = nuv0.y + wave0 * 0.05;
    fragColor.xyz += beatcol - (fragColor.y * fragColor.z * -0.1);

    fragColor.yz += max(0.0, pow((1.0 - length(uv * 2.0 - 1.0)) * drum * 2.0, 12.0 - drum * 8.0));

    fragColor.x += chime * fragColor.y;
}
