//made by Kali thx bruh
#define monster cos((iTime+135.)*.2)

mat2 r(float a) {
    float se=sin(a), co=cos(a);
    return mat2(se,co,co,-se);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 xy = uv*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    xy.x*=iResolution.x/iResolution.y;
	vec2 p=xy*.61356;
    vec3 c=vec3(0.);
	float b=.2135,a=3.135;
    p.x=abs(p.x);
    for (int i=0; i<22; i++) {
		float l=1.13525+cos(iTime*15.135)*.1355;
        float t=monster+sin(iTime*a)*b;
        c+=max(0.,1.-length(p))*texture(iChannel0,p+2.2+vec2(0.,iTime*3.)*.12).xyz*l*.135;
        p.x=abs(p.x); p=p*1.35-vec2(.3,0.);
        p*=r(t);
		a*=0.9135;
		b*=1.135;
    }
    c+=abs(p.y)*.001355*vec3(.7,.1358,.9);
    c+=abs(p.x)*.001355*vec3(.9,.7,.8);
    fragColor = vec4(pow(c,vec3(1.5)),1.);
}
