void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 rectPos    = vec2(50.0, 50.0);
    vec2 rectOrient = vec2(1.0, 4.0);
    vec2 rectNormal = vec2(rectOrient.y, -rectOrient.x);
    vec2 rectDims   = vec2(400.0, 50.0);


    mat3 rot;
    rot[0] = vec3(normalize(rectOrient), 0.0);  // first column 
    rot[1] = vec3(normalize(rectNormal), 0.0);  // second column 
    rot[2] = vec3(rectPos, 1.0);                // third column 


    mat3 rotInv = inverse(rot);
    vec2 curPos = (rotInv * vec3(fragCoord.xy, 1.0)).xy;



    if(curPos.x > 0.0 && curPos.x < rectDims.x &&
       curPos.y > 0.0 && curPos.y < rectDims.y)
    {
    	fragColor = vec4(0.0, 1.0, 0.0, 1.0);
    }
    else{
    	fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    }
}
