#define S(v) smoothstep(pixels,0.,v)


#define transition 0.5

#define rot(a) mat2(C=cos(a),S=-sin(a),-S,C)
#define rand(co) fract(sin(mod(dot(co ,vec2(12.9898,78.233)),3.14))*43758.5453)
#define f(uv) texture(iChannel0,uv)




const highp float pi = 3.141592653589793;

void mainImage( out vec4 o, vec2 u ){

    vec2 uv = (2.*u-iResolution.xy)/iResolution.y;
    //uv.x+=iTime;
    if(uv.y<0.){
		uv.y+=iTime/2./iResolution.x*512.;
    }else{
		uv.y-=iTime/2./iResolution.x*512.;
	}

    float C,S,
          dist = length(2.*fract(uv)-1.), 
          r = rand(floor(uv));

    vec2 uv2=u/iResolution.xy*2.-1.;
    uv2.x*=iResolution.x/iResolution.y;

    uv2*=mix(f((uv+r) * rot(pi*r*2.)), 
            f(uv * rot(pi-1.)), 
            smoothstep(1.-transition,.93, dist)).rg/2.;


    float pixels=2./iResolution.y;
    vec3 col;



    col+=vec3(0,1,2)*S(abs(uv2.y));


    o = vec4(col,1.0);
}
