float fbm(vec2 p)
{
    mat2 m=mat2(.8,.6,-.6,.8);
    p/=300.;
    float f=.5*texture(iChannel0,p).x; p*=m*2.02;
    f+=.25*texture(iChannel0,p).x; p*=m*2.03;
    f+=.125*texture(iChannel0,p).x; p*=m*2.01;
	f+=.0625*texture(iChannel0,p).x;
    return f/.9375;
}

void mainImage( out vec4 O, in vec2 U )
{
    vec2 R=iResolution.xy,p=U/R.y,q=(U+U-R)/R.y;
    vec3 c=vec3(.1);
    float r=length(q),
          r2=length(p),
          a=atan(p.y,p.x);
    if(r<.8)
    {
        c=vec3(.1,.2,.8);
        float f=fbm(p*5.);
        c=mix(c,vec3(.3,.9,.1),f);
        a+=.9*fbm(3.*p)+.1*fbm(30.*p);
        f=smoothstep(.0,.8,fbm(vec2(6.*r2,20.*a)));
        c=mix(c,vec3(1),f);
        f=fbm(vec2(r2*7.,a*12.));
        c=mix(c,vec3(.9,.3,.2),f);
        f=smoothstep(.9,.0,r);
        c*=f;
        f=smoothstep(.5,.0,length(q-vec2(.3,.4)));
        c+=vec3(.9,.8,.7)*.8*f;
        f=smoothstep(.75,.8,r);
        c=mix(c,vec3(.1),f);
    }

    c = pow(c, vec3(1.3));

    O.xyz=c;
}
