vec3 paintCircle (vec2 uv, vec2 center, float rad, float width) {

    vec2 diff = center-uv;
	float distan = sqrt(dot(diff, diff));
    float circle = smoothstep(rad-width, rad, distan) - smoothstep(rad, rad+width, distan);
    return vec3(circle);
}

float chessboard(vec2 uv)
{
    uv = floor(uv*12.0); 
    return mod(uv.y + uv.x, 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 backgroundColour = vec4(chessboard(uv));

    vec2 center = vec2(0.5);
    float radius = 1.0;    
    vec3 color = paintCircle(uv, center, radius, 0.1);
    vec4 circleColour = vec4(uv, 0.3 + 0.8 * tan(iTime), 1.5);


    float beat = texture( iChannel0, vec2(0.0, 0.01 ) ).x;
    vec4 circle = vec4(paintCircle(uv, center, beat * 0.65, 0.05), 0.01);

    fragColor = mix(backgroundColour,circleColour*1.0, circle*4.0);

}
