float gaussk(vec2 p1, vec2 p2)
{
    vec2 d = p2 - p1;
    return exp(-dot(d, d) / 0.0185);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    
    vec2 p1 = vec2(0.5 + 0.1 * sin(0.25*iTime), 0.40);
    vec2 p2 = vec2(0.5 + 0.1 * cos(0.25*iTime + 1.0), 0.55);
    float val = -0.005 + 2.0*gaussk(uv, p1) + 8.0*gaussk(uv, p2);    
	fragColor = mix(vec4(1.0),
                    vec4(0.5 + 0.4*sin(0.50*iTime), 0.5+0.35*sin(0.01*iTime+1.0), 0.5+0.45*cos(0.20*iTime), 1.0),
                    1000.0*val);
}
