void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord.xy / iResolution.xy) * 2.0 - 1.0);
    bool A = dot(uv,uv) > (0.5 * 0.5); //should produce a black circle, does so
    bool B = dot(-uv,-uv) > (0.5 * 0.5); //should produce a black circle, produces a black screen
    bool C = length(uv) > 0.5; //should produce a black circle, TODO test
    bool D = length(-uv) > 0.5; //should produce a black circle, TODO test
    fragColor = vec4( A == B && C == D ); //this should produce a total white screen
}
